--
-- 表的结构 `a_article_cate`
--

CREATE TABLE `a_article_cate` (
  `id` int(10) UNSIGNED NOT NULL,
  `title` varchar(255) NOT NULL DEFAULT '' COMMENT '标题',
  `web_url` varchar(255) NOT NULL DEFAULT '' COMMENT '跳转地址',
  `is_display` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0显示 1不显示',
  `pid` int(11) NOT NULL DEFAULT '0' COMMENT '父级分类',
  `sort` int(11) NOT NULL DEFAULT '0' COMMENT '排序规则',
  `status` tinyint(1) NOT NULL DEFAULT '1'
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='文章分类表';

